mapper.insert:
INSERT INTO cross_sorting ( id, org_id, create_dms_code, create_dms_name, destination_dms_code, destination_dms_name, mix_dms_code, mix_dms_name, create_user_code, create_user_name, delete_user_code, delete_user_name, create_time, delete_time, yn ) SELECT SEQ_CROSS_SORTING.NEXTVAL, A.* FROM(
SELECT #{item.orgId,jdbcType=INTEGER} AS org_id, #{item.createDmsCode,jdbcType=INTEGER} AS create_dms_code, #{item.createDmsName,jdbcType=VARCHAR} AS create_dms_name, #{item.destinationDmsCode,jdbcType=INTEGER} AS destination_dms_code, #{item.destinationDmsName,jdbcType=VARCHAR} AS destination_dms_name, #{item.mixDmsCode,jdbcType=INTEGER} AS mix_dms_code, #{item.mixDmsName,jdbcType=VARCHAR} AS mix_dms_name, #{item.createUserCode,jdbcType=INTEGER} AS create_user_code, #{item.createUserName,jdbcType=VARCHAR} AS create_user_name, #{item.deleteUserCode,jdbcType=INTEGER} AS delete_user_code, #{item.deleteUserName,jdbcType=VARCHAR} AS delete_user_name, #{item.createTime,jdbcType=TIMESTAMP} AS create_time, #{item.deleteTime,jdbcType=TIMESTAMP} AS delete_time, #{item.yn,jdbcType=INTEGER} AS yn FROM dual
) A

mapper.update:
UPDATE cross_sorting 
SET yn = 0, delete_user_code = #{deleteUserCode,jdbcType=INTEGER},
    delete_user_name = #{deleteUserName,jdbcType=VARCHAR},
    delete_time = #{deleteTime,jdbcType=TIMESTAMP}
WHERE id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT
id, org_id, create_dms_code, destination_dms_code,
mix_dms_code, mix_dms_name
FROM cross_sorting
WHERE yn = 1
AND create_dms_code = #{createDmsCode,jdbcType=INTEGER}
AND destination_dms_code = #{destinationDmsCode,jdbcType=INTEGER}
AND mix_dms_code = #{mixDmsCode,jdbcType=INTEGER}
