mapper.insert:
INSERT INTO load_bill ( id, load_id, warehouse_id, waybill_code, package_barcode, package_amount, order_id, box_code, dms_code, dms_name, send_time, send_code, truck_no, approval_code, approval_time, ctno, gjno, tpl, weight, create_time, update_time, gen_time, create_user_code, create_user, package_user_code, package_user, package_time, remark, yn ) SELECT SEQ_LOAD_BILL.NEXTVAL, A.* FROM(
SELECT #{item.loadId,jdbcType=VARCHAR} AS load_id, #{item.warehouseId,jdbcType=VARCHAR} AS warehouse_id, #{item.waybillCode,jdbcType=VARCHAR} AS waybill_code, #{item.packageBarcode,jdbcType=VARCHAR} AS package_barcode, #{item.packageAmount,jdbcType=INTEGER} AS package_amount, #{item.orderId,jdbcType=VARCHAR} AS order_id, #{item.boxCode,jdbcType=VARCHAR} AS box_code, #{item.dmsCode,jdbcType=INTEGER} AS dms_code, #{item.dmsName,jdbcType=VARCHAR} AS dms_name, #{item.sendTime,jdbcType=TIMESTAMP} AS send_time, #{item.sendCode,jdbcType=VARCHAR} AS send_code, #{item.truckNo,jdbcType=VARCHAR} AS truck_no, #{item.approvalCode,jdbcType=VARCHAR} AS approval_code, #{item.approvalTime,jdbcType=TIMESTAMP} AS approval_time, #{item.ctno,jdbcType=VARCHAR} AS ctno, #{item.gjno,jdbcType=VARCHAR} AS gjno, #{item.tpl,jdbcType=VARCHAR} AS tpl, #{item.weight,jdbcType=DOUBLE} AS weight, sysdate AS create_time, sysdate AS update_time, sysdate AS gen_time, #{item.createUserCode,jdbcType=INTEGER} AS create_user_code, #{item.createUser,jdbcType=VARCHAR} AS create_user, #{item.packageUserCode,jdbcType=INTEGER} AS package_user_code, #{item.packageUser,jdbcType=VARCHAR} AS package_user, #{item.packageTime,jdbcType=TIMESTAMP} AS package_time, #{item.remark,jdbcType=VARCHAR} AS remark, 1 AS yn FROM dual
) A

