mapper.select:
SELECT ID, PLAN_ID, ROUTE_TYPE, CREATE_SITE_CODE, CREATE_SITE_NAME, TRANSFER_SITE_CODE, TRANSFER_SITE_NAME, RECEIVE_SITE_CODE, RECEIVE_SITE_NAME, CREATE_USER, CREATE_USER_CODE, UPDATE_USER, UPDATE_USER_CODE, CREATE_TIME, UPDATE_TIME FROM AREA_DEST_CONFIG WHERE YN = 1
AND PLAN_ID = #{planId, jdbcType=BIGINT}
AND ROUTE_TYPE = #{routeType, jdbcType=BIGINT}
ORDER BY ROUTE_TYPE DESC
LIMIT #{startIndex}, #{pageSize}

SELECT
COUNT(*)
FROM AREA_DEST_CONFIG
WHERE YN = 1

    AND PLAN_ID = #{planId, jdbcType=BIGINT}


    AND ROUTE_TYPE = #{routeType, jdbcType=BIGINT}

mapper.update:
UPDATE AREA_DEST_CONFIG SET
UPDATE_USER = #{updateUser, jdbcType=VARCHAR},
UPDATE_USER_CODE = #{updateUserCode, jdbcType=BIGINT},
YN = 0, UPDATE_TIME = NOW() WHERE YN = 1 AND CREATE_SITE_CODE = #{createSiteCode,jdbcType=BIGINT}
AND TRANSFER_SITE_CODE = #{transferSiteCode, jdbcType=BIGINT}
AND RECEIVE_SITE_CODE in
#{item}

UPDATE AREA_DEST_CONFIG SET
UPDATE_USER = #{updateUser, jdbcType=VARCHAR},
UPDATE_USER_CODE = #{updateUserCode, jdbcType=BIGINT},
YN = 1, UPDATE_TIME = NOW() WHERE YN = 0 AND CREATE_SITE_CODE = #{createSiteCode,jdbcType=BIGINT}
AND TRANSFER_SITE_CODE = #{transferSiteCode, jdbcType=BIGINT}
AND RECEIVE_SITE_CODE = #{receiveSiteCode, jdbcType=BIGINT}
