mapper.select:
        SELECT batch_code, create_site_code, create_time, update_time,create_user, create_user_code,
			update_user_code, update_user
        FROM batchinfo
        WHERE  create_site_code = #{createSiteCode,jdbcType=INTEGER}
        AND LIMIT 1
        AND yn = 1
        AND create_time<=#{operateTime, jdbcType=TIMESTAMP}
        AND (
              update_time>#{operateTime, jdbcType=TIMESTAMP}
              OR
              update_time=#{minTime, jdbcType=TIMESTAMP}
             )

SELECT batch_code, create_site_code, create_time, update_time,create_user, create_user_code,
update_user_code, update_user
FROM batchinfo
WHERE  create_site_code = #{createSiteCode,jdbcType=INTEGER}

	and create_time >= #{createTime, jdbcType=TIMESTAMP}


	and create_time <= #{updateTime, jdbcType=TIMESTAMP}

mapper.update:
UPDATE batchinfo
SET update_time = #{updateTime, jdbcType=TIMESTAMP}
WHERE  batch_code =  #{batchCode,jdbcType=VARCHAR}
