mapper.select:
SELECT batch_code, create_site_code, create_time, update_time,create_user, create_user_code, update_user_code, update_user FROM batchinfo WHERE create_site_code = #{createSiteCode,jdbcType=INTEGER}
and CREATE_TIME >= str_to_date(date_format(#{createTime, jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s')
and CREATE_TIME <= str_to_date(date_format(#{updateTime, jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s')

mapper.update:
UPDATE batchinfo
SET update_time = #{updateTime, jdbcType=TIMESTAMP}
WHERE  batch_code =  #{sendCode,jdbcType=VARCHAR}
