mapper.update:
UPDATE batch_send SET
  send_car_state=#{sendCarState,jdbcType=INTEGER},
  send_car_operate_time= #{sendCarOperateTime, jdbcType=TIMESTAMP}
  WHERE
      send_code=#{sendCode,jdbcType=VARCHAR}
      AND send_car_operate_time < str_to_date(date_format(#{sendCarOperateTime, jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s')
      AND send_car_state<>#{sendCarState,jdbcType=INTEGER}

mapper.select:
SELECT batch_code, create_site_code, receive_site_code,send_code,create_time, update_time,send_status,yn,send_car_operate_time,send_car_state FROM batch_send WHERE create_site_code = #{createSiteCode,jdbcType=INTEGER}
and receive_site_code in(${receiveCodes})
and batch_code = #{batchCode,jdbcType=VARCHAR}
and create_time >= str_to_date(date_format(#{createTime, jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s')
and create_time <= str_to_date(date_format(#{updateTime, jdbcType=TIMESTAMP}, '%Y-%m-%d-%H:%i:%s'), '%Y-%m-%d %H:%i:%s')
and batch_code !=send_code and send_status=0

mapper.update:
UPDATE batch_send
SET update_time = NOW(),send_status=1
WHERE  send_code =  #{sendCode,jdbcType=VARCHAR}
