mapper.select:
SELECT box_id, box_code
FROM Box
WHERE yn = 1 AND box_status = 0 AND times = 0 AND box_type = #{type}
    AND create_site_code = #{createSiteCode}
	AND receive_site_code = #{receiveSiteCode}
	 LIMIT #{quantity}

mapper.update:
UPDATE Box
SET box_status = #{status}, update_time = NOW(),
	update_user = #{updateUser,jdbcType=VARCHAR}, update_user_code = #{updateUserCode,jdbcType=VARCHAR}
WHERE yn = 1 AND box_code in (${codes})
	AND box_status < #{status}

UPDATE Box
SET box_status = #{status,jdbcType=INTEGER}
WHERE box_code in ${code}
