mapper.update:
UPDATE
	collection_record_detail


		collected_status = #{collectedStatus, jdbcType=INTEGER},
		collected_time = NOW(),


		collected_mark = #{collectedMark, jdbcType=VARCHAR}



	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and scan_code in

		#{scanCode, jdbcType=VARCHAR}

	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1

mapper.select:
SELECT
	collection_code, agg_code, agg_code_type, init_number, is_collected, is_extra_collected, is_more_collected_mark, agg_mark
FROM
	collection_record

	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}

		and is_collected = #{isCollected, jdbcType=INTEGER}


		and is_extra_collected = #{isExtraCollected, jdbcType=INTEGER}


		and is_init = #{isInit, jdbcType=INTEGER}

	and yn = 1

SELECT
	collection_code, scan_code, scan_code_type, agg_code, agg_code_type, collected_status, collected_mark
FROM
	collection_record_detail

	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and scan_code = #{scanCode, jdbcType=VARCHAR}

		and agg_code = #{aggCode, jdbcType=VARCHAR}
		and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}


		and collected_status = #{collectedStatus, jdbcType=VARCHAR}

	and yn = 1

SELECT
	collection_code, scan_code, scan_code_type, agg_code, agg_code_type, collected_status, collected_mark
FROM
	collection_record_detail

	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and scan_code in

		#{scanCode, jdbcType=VARCHAR}

	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1

SELECT count(*) FROM ( SELECT cr.agg_code FROM collection_record_detail crd INNER JOIN collection_record cr ON crd.collection_code = cr.collection_code AND crd.agg_code = cr.agg_code AND crd.agg_code_type = cr.agg_code_type WHERE crd.yn = 1 AND cr.yn = 1 AND cr.is_init = 1
AND cr.is_collected = #{isCollected, jdbcType=VARCHAR}
AND cr.is_more_collected_mark = #{isMoreCollectedMark, jdbcType=VARCHAR}
AND crd.collected_mark = #{collectedMark, jdbcType=VARCHAR} AND crd.agg_code_type = #{aggCodeType, jdbcType=VARCHAR} AND crd.collection_code IN
#{collectionCode, jdbcType=VARCHAR}
GROUP BY cr.agg_code ) t

SELECT
 	max(ts)
FROM
	collection_record_detail

	collection_code IN

		#{collectionCode, jdbcType=VARCHAR}

	and collected_mark = #{collectedMark, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1

SELECT
	max(ts)
FROM
	collection_record_detail

	collection_code IN

		#{collectionCode, jdbcType=VARCHAR}

	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1

select
	cr.collection_code as collectionCode, cr.agg_code as aggCode, cr.agg_mark as aggMark
from
	collection_record cr inner join collection_record_detail crd
on
	cr.collection_code = crd.collection_code and cr.agg_code = crd.agg_code and cr.agg_code_type = crd.agg_code_type

	crd.collection_code in

		#{collectionCode, jdbcType=VARCHAR}

	and crd.collected_mark = #{collectedMark, jdbcType=VARCHAR}
	and crd.agg_code_type = #{aggCodeType, jdbcType=VARCHAR}

		and cr.agg_code = #{aggCode, jdbcType=VARCHAR}

	and cr.is_init = 1
	and cr.yn = 1 and crd.yn = 1

		and cr.is_more_collected_mark = #{isMoreCollectedMark, jdbcType=INTEGER}


		and cr.is_collected = #{isCollected, jdbcType=INTEGER}


		and cr.is_extra_collected = #{isExtraCollected, jdbcType=INTEGER}


group BY cr.collection_code, cr.agg_code, cr.agg_mark
order by aggMark
limit #{offset, jdbcType=INTEGER}, #{limit, jdbcType=INTEGER}

SELECT
	collection_code, agg_code, agg_code_type, scan_code, scan_code_type, collected_status, collected_mark, ts
FROM
	collection_record_detail

	collection_code in

		#{collectionCode, jdbcType=VARCHAR}

	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1

order by collected_mark
limit #{offset, jdbcType=INTEGER}, #{limit, jdbcType=INTEGER}
