mapper.select:
SELECT
	collection_code, agg_code, agg_code_type, sum, is_collected, is_extra_collected, agg_mark
FROM
	collection_record

	collection_code = #{collectionCode, jdbcType=VARCHAR}

		and agg_code = #{aggCode, jdbcType=VARCHAR}
		and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}


		and is_collected = #{isCollected, jdbcType=INTEGER}


		and is_extra_collected = #{isExtraCollected, jdbcType=INTEGER}

	and yn = 1

SELECT
	collection_code, scan_code, scan_code_type, agg_code, agg_code_type, collected_status, collected_mark
FROM
	collection_record_detail

	collection_code = #{collectionCode, jdbcType=VARCHAR}

		and scan_code = #{scanCode, jdbcType=VARCHAR}


		and agg_code = #{aggCode, jdbcType=VARCHAR}
		and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}


		and collected_status = #{collectedStatus, jdbcType=VARCHAR}

	and yn = 1

SELECT
	count(distinct agg_code)
FROM
	collection_record_detail

	collection_code in

		#{collectionCode, jdbcType=VARCHAR}

	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}

		and collected_mark = #{collectedMark, jdbcType=VARCHAR}

	and collected_status = 0
	and yn = 1

SELECT
	collection_code, agg_code, agg_code_type, collected_status, count(collected_status) as num
FROM
	collection_record_detail

	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1

group by
	agg_code, agg_code_type, collection_code, collected_status

SELECT
	collection_code, agg_code, agg_code_type, count(scan_code) as sumScanNum, sum(is_collected) as collectedNum,
sum(is_extra_collected) as extraCollectedNum, max(ts) as ts
FROM
	collection_record_detail

	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1

group by agg_code, agg_code_type, collection_code

select collection_code as collectionCode, count(agg_code) as sumScanNum, sum(is_collected) as collectedNum, sum(is_extra_collected) as extraCollectedNum, max(ts) as ts FROM collection_record where collection_code in
#{collectionCode, jdbcType=VARCHAR}
and agg_code_type = #{aggCodeType, jdbcType=VARCHAR} and yn = 1 group by collection_code

select crd.collection_code as collectionCode, crd.agg_code as aggCode, crd.agg_code_type as aggCodeType, crd.collected_mark as collectedMark, cr.agg_mark as aggMark, crd.collected_status as collectedStatus, count(1) as number, max(crd.ts) as ts from collection_record cr inner join collection_record_detail crd on cr.collection_code = crd.collection_code and cr.agg_code = crd.agg_code and cr.agg_code_type = crd.agg_code_type
crd.collection_code in
#{collectionCode, jdbcType=VARCHAR}
and cr.is_collected = #{isCollected, jdbcType=VARCHAR}
and cr.agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
and cr.agg_code = #{aggCode, jdbcType=VARCHAR}
and crd.yn = 1
group by crd.collection_code, crd.agg_code, crd.agg_code_type, collected_mark, crd.collected_status order by cr.agg_mark limit #{offset, jdbcType=INTEGER}, #{limit, jdbcType=INTEGER}

SELECT collection_code as collectionCode, agg_code as aggCode, agg_code_type as aggCodeType, collected_status as collectedStatus, collected_mark as collectedMark, count(1) as num , max(ts) as ts, FROM collection_record_detail
collection_code in
#{collectionCode, jdbcType=VARCHAR}
and collected_mark = #{collectedMark, jdbcType=VARCHAR}
and yn = 1
group by collection_code, agg_code, agg_code_type, collected_status, collected_mark

SELECT
	collection_code, agg_code, agg_codeType, collected_mark, count(1) as number
FROM
	collection_record_detail

	collection_code in

		#{collectionCode, jdbcType=VARCHAR}

	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_codeType = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1

order by collected_mark
limit #{offset, jdbcType=INTEGER}, #{limit, jdbcType=INTEGER}
