mapper.update:
UPDATE
	collection_record


		is_collected = #{isCollected, jdbcType=INTEGER}


		and is_extra_collected = #{isExtraCollected, jdbcType=INTEGER}


		and agg_mark = #{aggMark, jdbcType=VARCHAR}

	and update_time = NOW()


	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and yn = 1

UPDATE
	collection_record_detail


		collected_status = #{collectedStatus, jdbcType=INTEGER}


		and collected_mark = #{collectedMark, jdbcType=VARCHAR}

	and collected_time = NOW()


	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and scan_code = #{scanCode, jdbcType=VARCHAR}
	and yn = 1

mapper.select:
SELECT
	collection_code, agg_code, agg_code_type, sum, is_collected, is_extra_collected, agg_mark
FROM
	collection_record

	collection_code = #{collectionCode, jdbcType=VARCHAR}

		and aggCode = #{aggCode, jdbcType=VARCHAR}
		and aggCodeType = #{aggCodeType, jdbcType=VARCHAR}


		and is_collected = #{isCollected, jdbcType=INTEGER}


		and is_extra_collected = #{isExtraCollected, jdbcType=INTEGER}

	and yn = 1

SELECT
	collection_code, scan_code, scan_code_type, agg_code, agg_code_type, collected_status, collected_mark
FROM
	collection_record_detail

	collection_code = #{collectionCode, jdbcType=VARCHAR}

		and scanCode = #{scanCode, jdbcType=VARCHAR}


		and aggCode = #{aggCode, jdbcType=VARCHAR}
		and aggCodeType = #{aggCodeType, jdbcType=VARCHAR}


		and collected_status = #{collectedStatus, jdbcType=VARCHAR}

	and yn = 1

SELECT
	collection_code, agg_code, agg_code_type, count(scan_code) as sumScanNum, sum(is_collected) as collectedNum,
sum(is_extra_collected) as extraCollectedNum
FROM
	collection_record_detail

	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1

SELECT
	collected_mark, count(collected_mark) as num
FROM
	collection_record_detail

	collection_code = #{collectionCode, jdbcType=VARCHAR}
	and agg_code = #{aggCode, jdbcType=VARCHAR}
	and agg_code_type = #{aggCodeType, jdbcType=VARCHAR}
	and yn = 1
group by collected_mark
