mapper.update:
update waybill_consumable_relation
	set is_delete = 1
where id = #{id,jdbcType=BIGINT}

mapper.select:
select wcr.id,waybill_code,receive_quantity,confirm_quantity,pack_user_erp,consumable_code,name,type,type_name,volume,volume_coefficient,specification,unit,wcr.operate_time,wcr.operate_user_code,wcr.operate_user_erp,wcr.create_time,wcr.update_time,wcr.is_delete
	from packing_consumable_info pci
	inner join waybill_consumable_relation wcr
	on pci.code = wcr.consumable_code
	where pci.is_delete = 0 and wcr.is_delete = 0 and waybill_code = #{waybillCode,jdbcType=VARCHAR}
limit #{offset,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}
