sql.where:
yn = 1

	AND create_user_name like '%'||#{createUserName,jdbcType=VARCHAR}||'%'


	AND org_id = #{orgId,jdbcType=INTEGER}


	AND create_dms_code = #{createDmsCode,jdbcType=INTEGER}


	AND destination_dms_code = #{destinationDmsCode,jdbcType=INTEGER}

mapper.select:

	select * from ( select t.*, rownum l from (

