sql.where:
yn = 1

	AND create_user = #{createUser,jdbcType=VARCHAR}


		and create_time BETWEEN #{startTime} and #{endTime}


		and     create_time >= #{startTime}  


		and     create_time <= #{endTime}

mapper.select:
SELECT
FROM dms_export_log
