INSERT INTO load_bill_report ( report_id, load_id, warehouse_id, process_time, status, order_id, notes, yn ) SELECT A.* FROM(
SELECT #{item.reportId,jdbcType=VARCHAR} AS report_id, #{item.loadId,jdbcType=VARCHAR} AS load_id, #{item.warehouseId,jdbcType=VARCHAR} AS warehouse_id, #{item.processTime,jdbcType=TIMESTAMP} AS process_time, #{item.status,jdbcType=INTEGER} AS status, #{item.orderId,jdbcType=VARCHAR} AS order_id, #{item.waybillCode,jdbcType=VARCHAR} AS waybill_code, #{item.notes,jdbcType=VARCHAR} AS notes, 1 AS yn FROM dual
) A
