select end_site_id, count(*) board_count
from jy_biz_task_comboard
where start_site_id = #{startSiteId}
  and end_site_id in

  #{item}

  and board_status in

  #{item}

and yn = 1
AND create_time > #{createTime}
and comboard_source in

  #{item}

