mapper.select:
SELECT

FROM jy_biz_task_exception
WHERE
yn = 1
AND site_code = #{siteId}
AND status = #{status}
AND distribution_target = #{gridRid}

    AND floor = #{floor}


    AND grid_code = #{gridCode}


    AND processing_status = #{processingStatus}


    AND handler_erp = #{handlerErp}


    AND    ts >  DATE_SUB(CURDATE(), INTERVAL #{limitDay} DAY) 

order by id desc

    LIMIT #{offSet}, #{pageSize};

select 
FROM jy_biz_task_exception
WHERE
    yn = 1 and type = 1
and handler_erp = #{handlerErp}
and     process_begin_time >= #{queryStartTime}  
and     process_begin_time <= #{queryEndTime}

select count(*)
FROM jy_biz_task_exception
WHERE
yn = 1 and type = 1
and site_code = #{siteCode}
and     process_begin_time >= #{processBeginTime}

SELECT
site_code AS siteCode,
site_name AS siteName,
floor AS floor,
grid_code AS gridCode,
grid_no AS gridNo,
area_code AS areaCode,
area_name AS areaName,
count(*) AS pendingNum,
SUM(time_out) AS timeoutNum
FROM jy_biz_task_exception
WHERE
yn = 1
AND site_code = #{siteId}
AND type = #{type}
AND status = #{status}
AND distribution_target = #{gridRid}
AND distribution_time  >=  #{timeOutTime}
GROUP BY grid_code

    LIMIT #{offSet}, #{pageSize};

SELECT

FROM jy_biz_task_exception
WHERE
yn = 1
AND site_code = #{siteId}
AND type = #{type}
AND status = #{status}
AND floor = #{floor}
AND grid_code = #{gridCode}
AND grid_no = #{gridNo}
AND area_code = #{areaCode}
AND distribution_target = #{gridRid}
AND distribution_time  >=  #{timeOutTime}
