mapper.select:
select 
from jy_biz_task_picking_good
where biz_id = #{bizId}

    and yn = #{yn}

limit 1

select  
from    jy_biz_task_picking_good
where   yn = 1
and     next_site_id = #{pickingSiteId}
and     status = #{status}
and     task_type = #{taskType}
and     create_time >= #{createTime}
group by end_node_code
limit #{offset}, #{limit}

select  
from    jy_biz_task_picking_good
where   yn = 1
and     next_site_id = #{pickingSiteId}
and     status = #{status}
and     task_type = #{taskType}
and     create_time >= #{createTime}

and     end_node_code in

    #{item}


limit #{offset}, #{limit}

select  count(*) as count,
        status
from    jy_biz_task_picking_good
where   yn = 1
and     next_site_id = #{pickingSiteId}
and     task_type = #{taskType}
and     create_time >= #{createTime}
group by status

mapper.update:
update  jy_biz_task_picking_good
set     complete_node = #{completeNode},
        status = #{status}
where   yn = 1
and     biz_id in

  #{item}

mapper.select:
select biz_id
from jy_biz_task_picking_good
where yn = 1
and create_time  >=  #{startTime}
and create_time  <=  #{endTime}
and task_type = #{taskType}
and status  <  #{status}
and manual_created_flag = 1
limit #{offset}, #{limit}

select  
from    jy_biz_task_picking_good
where   yn = 1
and     next_site_id = #{pickingSiteId}
and     task_type = #{taskType}
and     create_time  >=  #{createTime}
