mapper.select:
select

from jy_biz_task_unload_vehicle_stage
where unload_vehicle_biz_id = #{unloadVehicleBizId} and yn=1

  and status =#{status}


  and type =#{type}

  limit 1

select count(*)
from jy_biz_task_unload_vehicle_stage
where yn=1 and unload_vehicle_biz_id = #{unloadVehicleBizId} and

  and status =#{status}


  and type =#{type}
