mapper.select:
       select SUM(actual_scan_waybill_count)   actual_scan_waybill_count
FROM uat_jy_unload_aggs
WHERE biz_id = #{bizId}
  and board_code = #{boardCode}
  and yn = 1

SELECT product_type type,SUM(should_scan_count) shouldScanCount, SUM(actual_scan_count) haveScanCount, SUM(more_scan_total_count) moreScanTotalCount from uat_jy_unload_aggs WHERE biz_id = #{bizId}
AND board_code =#{boardCode}
GROUP BY product_type

SELECT SUM(should_scan_count) shouldScanCount, SUM(actual_scan_count) haveScanCount,
SUM(more_scan_total_count) extraScanCount, SUM(intercept_actual_scan_count) interceptCount FROM uat_jy_unload_aggs WHERE biz_id = #{bizId}
AND board_code =#{boardCode}

SELECT biz_id, board_code, sum(actual_scan_count) actual_scan_count, sum(more_scan_total_count) more_scan_total_count
FROM  uat_jy_unload_aggs
WHERE yn = 1 and biz_id = #{bizId} and board_code =#{boardCode}
