mapper.select:
select

from load_scan_record


        and id = #{id,jdbcType=BIGINT}


        and task_id = #{taskId,jdbcType=BIGINT}


        and waybill_code = #{wayBillCode,jdbcType=VARCHAR}


        and package_code = #{packageCode,jdbcType=VARCHAR}


        and board_code = #{boardCode,jdbcType=INTEGER}


        and scan_action = #{scanAction,jdbcType=INTEGER}


        and transfer = #{transfer,jdbcType=INTEGER}


        and create_user_code = #{createUserCode,jdbcType=VARCHAR}


        and create_user_name = #{createUserName,jdbcType=VARCHAR}


        and #{updateUserCode,jdbcType=VARCHAR}


        and update_user_code = update_user_name = #{updateUserName,jdbcType=VARCHAR}


        and create_time = #{createTime,jdbcType=TIMESTAMP}


        and update_time = #{updateTime,jdbcType=TIMESTAMP}


        and yn = #{yn,jdbcType=INTEGER}

mapper.update:
update load_scan_record
task_id = #{taskId,jdbcType=BIGINT}, waybill_code = #{wayBillCode,jdbcType=VARCHAR}, package_code = #{packageCode,jdbcType=VARCHAR}, board_code = #{boardCode,jdbcType=INTEGER}, scan_action = #{scanAction,jdbcType=INTEGER}, transfer = #{transfer,jdbcType=INTEGER}, create_user_code = #{createUserCode,jdbcType=VARCHAR}, create_user_name = #{createUserName,jdbcType=VARCHAR}, update_user_code = #{updateUserCode,jdbcType=VARCHAR}, update_user_name = #{updateUserName,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_time = #{updateTime,jdbcType=TIMESTAMP}, flow_disaccord = #{flowDisaccord,jdbcType=INTEGER}, force_status = #{forceStatus,jdbcType=INTEGER}, yn = #{yn,jdbcType=INTEGER},
where id = #{id,jdbcType=BIGINT}
