sql.where:
yn = 1

    AND create_site_code = #{createSiteCode,jdbcType=BIGINT}


    AND material_type = #{materialType,jdbcType=TINYINT}


    AND material_code = #{materialCode,jdbcType=VARCHAR}


    AND receive_code = #{boardCode,jdbcType=VARCHAR}


    AND update_user_erp = #{userErp,jdbcType=VARCHAR}


    AND update_time  >=  #{startTime,jdbcType=TIMESTAMP}


    AND update_time  <=  #{endTime,jdbcType=TIMESTAMP}

yn = 1

    AND create_site_code = #{createSiteCode,jdbcType=BIGINT}


    AND material_type = #{materialType,jdbcType=TINYINT}


    AND material_code = #{materialCode,jdbcType=VARCHAR}


    AND send_code = #{boardCode,jdbcType=VARCHAR}


    AND update_user_erp = #{userErp,jdbcType=VARCHAR}


    AND update_time  >=  #{startTime,jdbcType=TIMESTAMP}


    AND update_time  <=  #{endTime,jdbcType=TIMESTAMP}

mapper.select:
SELECT material_type, material_code, receive_code, create_site_code, update_user_erp, update_time, material_status, scan_type FROM (
SELECT material_code, material_type, receive_code, create_site_code, update_user_erp, update_time, 1 as material_status, 1 as scan_type FROM dms_material_receive
UNION
SELECT material_code, material_type, send_code, create_site_code, update_user_erp, update_time, 2 as material_status, 2 as scan_type FROM dms_material_send
) t ORDER BY material_code, update_time DESC LIMIT #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}
