mapper.select:
select id
from mini_store_bind_relation
where store_code = #{storeCode}
  and occupied_flag = 1 LIMIT 1;

select id
from mini_store_bind_relation
where box_code = #{boxCode}
  and occupied_flag = 1 LIMIT 1;

select id
from mini_store_bind_relation
where ice_board_code1 = #{iceBoardCode}
  and occupied_flag = 1
UNION ALL
select id
from mini_store_bind_relation
where ice_board_code2 = #{iceBoardCode}
  and occupied_flag = 1 LIMIT 1;

