mapper.select:
select

from
	dms_send_relation t
where yn = 1 
and original_site_code = #{originalSiteCode,jdbcType=INTEGER}
and destination_site_code = #{destinationSiteCode,jdbcType=INTEGER}

select

from
	dms_send_relation 
where yn = 1 
and original_site_code = #{originalSiteCode,jdbcType=INTEGER}
and ts  >=  #{startTime,jdbcType=TIMESTAMP}		

	and destination_site_code = #{destinationSiteCode,jdbcType=INTEGER}


	and destination_site_name = #{destinationSiteName,jdbcType=VARCHAR}


	and destination_cross_code = #{destinationCrossCode,jdbcType=VARCHAR}

limit #{limitNum,jdbcType=INTEGER}

mapper.sql:
	left outer join tms_vehicle_route s on 
	s.yn = 1 
	and s.original_site_code = #{originalSiteCode,jdbcType=INTEGER}
	and t.destination_site_code = s.destination_site_code
where t.yn = 1 
and t.original_site_code = #{originalSiteCode,jdbcType=INTEGER} 
and t.ts  >=  #{effectStartTime,jdbcType=TIMESTAMP} 

	and t.destination_site_code = #{destinationSiteCode,jdbcType=INTEGER}


	and t.destination_site_name = #{destinationSiteName,jdbcType=VARCHAR}


	and t.destination_cross_code = #{destinationCrossCode,jdbcType=VARCHAR}


	and s.vehicle_number = #{vehicleNumber,jdbcType=VARCHAR}


	and s.depart_time  >=  #{departStartTime,jdbcType=TIMESTAMP}


	and s.depart_time  <=  #{departEndTime,jdbcType=TIMESTAMP}
