mapper.select:
SELECT
FROM notice WHERE is_delete = 0 AND is_display = 1 ORDER BY is_top_display desc, create_time desc
LIMIT #{limit}

SELECT count(1)
FROM notice
WHERE is_delete = 0

    and theme like concat(#{title}, '%')


    and type = #{type}


    and level = #{level}


    and is_display = #{isDisplay}

ORDER BY is_top_display desc, create_time desc

SELECT

FROM notice
WHERE is_delete = 0

    and theme like concat(#{title}, '%')


    and type = #{type}


    and level = #{level}


    and is_display = #{isDisplay}

ORDER BY is_top_display desc, create_time desc

    LIMIT #{offset}, #{limit}

mapper.update:
UPDATE notice SET is_delete = 1,
update_user = #{updateUser},
update_time = NOW() WHERE is_delete = 0 AND id = #{id}
