mapper.select:
SELECT * FROM
   (SELECT rownum num,alldata.* FROM
      (SELECT

       FROM pop_print
      WHERE create_site_code=#{createSiteCode,jdbcType=INTEGER}
       AND create_time >= to_date(to_char(#{startTime, jdbcType=TIMESTAMP}, 'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss')
       AND create_time <= to_date(to_char(#{endTime, jdbcType=TIMESTAMP}, 'yyyy-mm-dd hh24:mi:ss'), 'yyyy-mm-dd hh24:mi:ss')

       AND pop_receive_type = #{popReceiveType,jdbc="INTEGER"}


        AND waybill_type = #{waybillType,jdbc="INTEGER"}


       AND queue_no = #{queueNo,jdbcType=VARCHAR}

      ORDER BY id ) alldata
WHERE  LIMIT #{endIndex, jdbcType=INTEGER}  )
WHERE  num > #{startIndex, jdbcType=INTEGER}

        SELECT count(p.id)
        FROM pop_print p
        WHERE create_site_code=#{createSiteCode,jdbcType=INTEGER}
          AND create_time >= to_date(to_char(#{startTime, jdbcType=TIMESTAMP}, 'yyyy-mm-dd hh24:mi:ss'), 'yyyy-mm-dd hh24:mi:ss')
		AND create_time <= to_date(to_char(#{endTime, jdbcType=TIMESTAMP}, 'yyyy-mm-dd hh24:mi:ss'), 'yyyy-mm-dd hh24:mi:ss')

            AND pop_receive_type = #{popReceiveType,jdbc="INTEGER"}


            AND waybill_type = #{waybillType,jdbc="INTEGER"}


            AND queue_no = #{queueNo,jdbcType=VARCHAR}


    select * from ( select t.*, rownum l from (

select
p.waybill_code, p.create_site_code, p.print_pack_code,
p.print_pack_time, p.print_invoice_code, p.print_invoice_time
from pop_print p
left join inspection i on p.waybill_code = i.waybill_code and i.inspection_type = 40

order by p.id desc

    ) t where  LIMIT #{endIndex, jdbcType=INTEGER} 
    ) where l > #{startIndex, jdbcType=INTEGER}
