sql.where:
  			YN = 1

				and WAYBILL_CODE = #{waybillCode, jdbcType=VARCHAR}


				and THIRD_WAYBILL_CODE = #{thirdWaybillCode, jdbcType=VARCHAR}


				and CREATE_SITE_CODE = #{createSiteCode, jdbcType=INTEGER}


				and CREATE_SITE_NAME = #{createSiteName, jdbcType=VARCHAR}


				and OPERATOR_CODE = #{operatorCode, jdbcType=INTEGER}


				and OPERATOR_NAME = #{operatorName, jdbcType=VARCHAR}


				and CREATE_TIME >= str_to_date(date_format(#{startTime, jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s')


				and CREATE_TIME <= str_to_date(date_format(#{endTime, jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s')


			AND  OPERATE_TIME>=str_to_date(#{operateStartTime},'%Y-%m-%d %H:%i:%s')


			AND  OPERATE_TIME<=str_to_date(#{operateEndTime},'%Y-%m-%d %H:%i:%s')
