sql.where:
yn = 1 and receive_type = 60 and create_site_code = #{createSiteCode, jdbcType=INTEGER} and create_time >= str_to_date(date_format(#{startTime, jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s') and create_time <= str_to_date(date_format(#{endTime, jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s')
and create_user like concat(#{createUser, jdbcType=VARCHAR},'%')
and queueno = #{queueNo, jdbcType=VARCHAR}
