mapper.select:
SELECT rule_id, rule_type, content, in_out, site_code, memo, create_time, update_time, ts
FROM Rules
WHERE yn = 1

    AND site_code = #{siteCode}


    AND rule_type = #{type}

LIMIT 1

SELECT rule_id, rule_type, content, in_out, site_code, memo, create_time, update_time, ts
FROM Rules
WHERE yn = 1

    AND site_code = #{siteCode}


    AND rule_type = #{type}

ORDER BY site_code, rule_type

