mapper.update:
UPDATE seal_vehicle set yn = 0 



			and system_id = #{id,jdbcType=INTEGER}



				and seal_code = #{code,jdbcType=VARCHAR} 


				and create_site_code = #{createSiteCode,jdbcType=INTEGER},



	and yn = 1

mapper.insert:
INSERT INTO seal_vehicle ( vehicle_code, seal_code, driver_code, driver, create_site_code, receive_site_code, create_user_code, create_user, update_user_code, update_user, create_time, update_time, yn, send_code, volume, weight, package_num ) select A.* FROM(
SELECT #{item.vehicleCode,jdbcType=VARCHAR} AS vehicle_code, #{item.code,jdbcType=VARCHAR} AS seal_code, #{item.driverCode,jdbcType=VARCHAR} AS driver_code, #{item.driver,jdbcType=VARCHAR} AS driver, #{item.createSiteCode,jdbcType=INTEGER} AS create_site_code, #{item.receiveSiteCode,jdbcType=INTEGER} AS receive_site_code, #{item.createUserCode,jdbcType=INTEGER} AS create_user_code, #{item.createUser,jdbcType=VARCHAR} AS create_user, #{item.updateUserCode,jdbcType=INTEGER} AS update_user_code, #{item.updateUser,jdbcType=VARCHAR} AS update_user, #{item.createTime,jdbcType=TIMESTAMP} AS create_time, #{item.updateTime,jdbcType=TIMESTAMP} AS update_time, #{item.yn,jdbcType=INTEGER} AS yn, #{item.sendCode,jdbcType=VARCHAR} AS send_code, #{item.volume,jdbcType=DOUBLE} AS volume, #{item.weight,jdbcType=DOUBLE} AS weight, #{item.packageNum,jdbcType=INTEGER} AS package_num FROM dual
) A
