mapper.select:
select
from user_sign_record r
where r.yn = 1 and r.id = #{id,jdbcType=BIGINT}

sql.where:
r.yn = 1 

and r.sign_date = #{signDate,jdbcType=DATE}


and r.org_code = #{orgCode,jdbcType=INTEGER}


and r.site_code = #{siteCode,jdbcType=INTEGER}


and r.wave_code = #{waveCode,jdbcType=TINYINT}


and r.create_user = #{createUser,jdbcType=VARCHAR}


and r.sign_date >= #{signDateStart,jdbcType=DATE}


and r.sign_date <= #{signDateEnd,jdbcType=DATE}


and g.grid_no = #{gridNo,jdbcType=VARCHAR}


and g.grid_code = #{gridCode,jdbcType=VARCHAR}


and s.area_code = #{areaCode,jdbcType=VARCHAR}


and s.work_code = #{workCode,jdbcType=VARCHAR}

mapper.select:
select r.sign_date,r.wave_code,g.org_code,g.org_name,g.site_code,g.site_name, g.floor,g.grid_no,g.grid_code,g.grid_name, s.area_code,s.area_name,s.work_code,s.work_name, p.plan_name, g.standard_num, case when p.plan_attend_num is not null then p.plan_attend_num else 0 end plan_attend_num, r.attend_num, case when r2.now_attend_num is not null then r2.now_attend_num else 0 end now_attend_num from (select t.sign_date,t.ref_grid_key,t.wave_code,max(t.ref_plan_key) ref_plan_key,max(t.ref_station_key) ref_station_key, count(distinct t.user_code) attend_num from user_sign_record t
group by t.sign_date,t.ref_grid_key,t.wave_code) r
limit #{offset}, #{limit}

select sum(u.standard_num_sum) standard_num_sum, sum(u.plan_attend_num_sum) plan_attend_num_sum, sum(u.attend_num_sum) attend_num_sum, sum(u.now_attend_num_sum) now_attend_num_sum from( select 0 standard_num_sum, sum(case when p.plan_attend_num is null then 0 else p.plan_attend_num end) plan_attend_num_sum, sum(r.attend_num) attend_num_sum, sum(case when r2.now_attend_num is not null then r2.now_attend_num else 0 end) now_attend_num_sum from( (select t.sign_date,t.ref_grid_key,t.wave_code,max(t.ref_plan_key) ref_plan_key,max(t.ref_station_key) ref_station_key, count(distinct t.user_code) attend_num from user_sign_record t
group by t.sign_date,t.ref_grid_key,wave_code) r left outer join work_station s on r.ref_station_key = s.business_key and s.yn = 1 left outer join work_station_grid g on r.ref_grid_key = g.business_key and g.yn = 1 left outer join work_station_attend_plan p on r.ref_grid_key = p.ref_grid_key and r.wave_code = p.wave_code and p.yn = 1
)
union all select sum(case when g.standard_num is null then 0 else g.standard_num end) standard_num_sum, 0 plan_attend_num_sum, 0 attend_num_sum, 0 now_attend_num_sum from (select t.ref_grid_key,max(t.ref_station_key) ref_station_key from user_sign_record t
group by t.ref_grid_key) r left outer join work_station_grid g on r.ref_grid_key = g.business_key and g.yn = 1 left outer join work_station s on r.ref_station_key = s.business_key and s.yn = 1
) u

