mapper.select:
select

from
	user_sign_record r

where r.yn = 1
	and r.sign_date = #{signDate,jdbcType=DATE}
	and	r.site_code = #{siteCode,jdbcType=INTEGER}
	and	g.grid_no = #{gridNo,jdbcType=VARCHAR}
	and g.floor = #{floor,jdbcType=INTEGER}
	and s.area_code = #{areaCode,jdbcType=VARCHAR}		
	and s.work_code = #{workCode,jdbcType=VARCHAR}			
	and r.wave_code = #{waveCode,jdbcType=VARCHAR}
	and r.user_code = #{userCode,jdbcType=VARCHAR}

select

from
	user_sign_record r

where r.yn = 1
	and	r.site_code = #{siteCode,jdbcType=INTEGER}
	and r.user_code = #{userCode,jdbcType=VARCHAR}

	and r.sign_date = #{signDate,jdbcType=DATE}

order by r.sign_in_time desc
limit 1
