mapper.update:
update storage_package_d
	set is_delete = 1
where id = #{id,jdbcType=BIGINT}

update storage_package_d
set is_delete = 1
where waybill_code = #{waybillCode,jdbcType=VARCHAR}

mapper.select:
select
from storage_package_d where waybill_code = #{waybillCode}
ORDER by putaway_time desc limit 1

select
from storage_package_d where package_code = #{packageCode}
ORDER by putaway_time desc limit 1

select
from storage_package_d where performance_code = #{performanceCode}
ORDER by putaway_time desc limit 1

select
from storage_package_d where waybill_code = #{waybillCode}
ORDER by putaway_time
