	AND task_status IN (0, 3,4) AND task_type = #{type}

		and own_sign = #{ownSign,jdbcType=VARCHAR}


		AND queue_id in

			#{queueId}


	 LIMIT
	#{fetchNum}

        SELECT task_id, task_type, box_code, create_site_code,
