sql.where:
yn = 1 AND task_status != 2

	AND task_type = #{taskType}


	AND task_status = #{taskStatus}


	AND execute_count = #{executeCount}


	AND keyword1 = #{keyword1}


	AND keyword2 = #{keyword2}


	AND execute_time >=
	#{startTime}


	AND execute_time <=
	#{endTime}

mapper.select:
SELECT TASK_ID, TASK_TYPE, TASK_STATUS, KEYWORD1, KEYWORD2, BODY, EXECUTE_TIME, OWN_SIGN FROM ${tableName}
ORDER BY EXECUTE_TIME
LIMIT #{startIndex},#{pageSize}

mapper.update:
UPDATE ${tableName}
SET TASK_STATUS = 0, EXECUTE_COUNT = 0
WHERE TASK_ID = #{id, jdbcType=INTEGER}

