mapper.update:
update ar_send_register
set is_delete = 1
where id = #{id,jdbcType=BIGINT}

mapper.select:
select
from ar_send_register where id in
#{item,jdbcType=BIGINT}

mapper.sql:
is_delete = 0

    and order_code = #{orderCode,jdbcType=VARCHAR}


    and transport_name = #{transportName,jdbcType=VARCHAR}


    and operation_dept = #{operationDept,jdbcType=VARCHAR}


    and send_date = #{sendDate,jdbcType=TIMESTAMP}


    and operation_time >= str_to_date(#{startOperTime, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')


    and operation_time <= str_to_date(#{endOperTime, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')

mapper.select:
select order_code,transport_name, start_city_id,start_city_name, start_station_id,start_station_name, end_city_id,end_city_name, end_station_id,end_station_name, plan_start_time,plan_end_time from ar_send_register where 1=1
and start_city_id = #{startCityId,jdbcType=INTEGER}
and end_city_id = #{endCityId, jdbcType=INTEGER}
and order_code = #{orderCode,jdbcType=VARCHAR}
and transport_name = #{transportName,jdbcType=VARCHAR}
and plan_end_time >= now()-INTERVAL 12 HOUR and plan_end_time <= NOW()+INTERVAL 1 DAY order by plan_end_time desc

select

from ar_send_register
where
transport_name = #{transportName,jdbcType=VARCHAR}
and DATE_FORMAT(send_date, '%Y-%m-%d') = DATE_FORMAT(#{sendDate,jdbcType=TIMESTAMP}, '%Y-%m-%d')

    and transport_type = #{transportType,jdbcType=SMALLINT}


    and site_order = #{siteOrder,jdbcType=VARCHAR}

order by create_time
