sql.where:
r.yn = 1 

and r.sign_date = #{signDate,jdbcType=DATE}


and r.org_code = #{orgCode,jdbcType=INTEGER}


and r.site_code = #{siteCode,jdbcType=INTEGER}


and r.grid_no = #{gridNo,jdbcType=VARCHAR}


and r.wave_code = #{waveCode,jdbcType=TINYINT}


and r.create_user = #{createUser,jdbcType=VARCHAR}


and r.sign_date >= #{signDateStart,jdbcType=DATE}


and r.sign_date <= #{signDateEnd,jdbcType=DATE}

mapper.select:
select rc.sign_date,g.org_code,g.org_name,rc.site_code,g.site_name,rc.grid_no,g.grid_name, s.work_code,s.work_name,rc.station_code,s.station_name,rc.wave_code, g.standard_num,p.plan_attend_num,rc.attend_num from (select r.sign_date,r.site_code,r.grid_no,r.station_code,r.wave_code, sum(case when r.sign_in_time is not null and r.sign_out_time is null then 1 else 0 end) attend_num from user_sign_record r
group by r.sign_date,r.site_code,r.grid_no,r.station_code,r.wave_code) rc left outer join work_station s on rc.station_code = s.station_code and s.yn = 1 left outer join work_station_grid g on rc.station_code = g.station_code and rc.site_code = g.site_code and rc.grid_no = g.grid_no and g.yn = 1 left outer join work_station_attend_plan p on rc.station_code = p.station_code and rc.site_code = p.site_code and rc.grid_no = p.grid_no and rc.wave_code = p.wave_code and p.yn = 1
limit #{startRecord}, #{pageSize}

sql.where:
r.yn = 1

and r.sign_date = #{signDate,jdbcType=DATE}


and r.org_code = #{orgCode,jdbcType=INTEGER}


and r.site_code = #{siteCode,jdbcType=INTEGER}


and r.grid_no = #{gridNo,jdbcType=VARCHAR}


and r.wave_code = #{waveCode,jdbcType=TINYINT}


and r.create_user = #{createUser,jdbcType=VARCHAR}


and r.sign_date >= #{signDateStart,jdbcType=DATE}


and r.sign_date <= #{signDateEnd,jdbcType=DATE}

mapper.select:
select

from
	user_sign_record t
where yn = 1
	and sign_date = #{signDate,jdbcType=DATE}
	and	site_code = #{siteCode,jdbcType=INTEGER}
	and	grid_no = #{gridNo,jdbcType=VARCHAR}
	and station_code = #{stationCode,jdbcType=VARCHAR}			
	and wave_code = #{waveCode,jdbcType=VARCHAR}
	and user_code = #{userCode,jdbcType=VARCHAR}

select

from
	user_sign_record t
where yn = 1
	and	site_code = #{siteCode,jdbcType=INTEGER}
	and user_code = #{userCode,jdbcType=VARCHAR}

	and sign_date = #{signDate,jdbcType=DATE}

order by sign_in_time desc
limit 1
